import React, {Component} from 'react'
/*
  blah
*/
import './App.css';
/*
  blah
*/
import ResultCom from './components/ResultCom'
import Calculator from './components/Calculator'

class App extends Component {
  constructor(){
    super();

    this.state = {
      answer: ""
    }
  }

  onClick = button => {
    if(button === "="){
      this.compute()
    } else if(button === "C") {
      this.reset()
    } else if(button === "CE"){
      this.backspace()
    } else {
      this.setState({
        answer: this.state.answer + button
      })
    }
  };

  compute = () => {
    try {
      this.setState({
        answer: (eval(this.state.answer) || "") + ""
      })
    } catch (e) {
      this.setState({
        answer: "error"
      })
    }
  };

  reset = () => {
    this.setState({
      answer: ""
    })
  };

  backspace = () => {
    this.setState({ 
      answer: this.state.answer.slice(0, -1)
    })
  };

  render() {
    return (
      <div>
        <div className="calc-body">
          <h1>Calculator App from Aedan</h1>
          <ResultCom answer={this.state.answer}/>
          <Calculator onClick={this.onClick}/>
        </div>
      </div>
    );
  }
}

export default App;
